
/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function (s) {
    for (let length = s.length; length > 0; length--) {
        for (let start = 0; start <= s.length - length; start++) {
            let tempResult = s.substring(start, length + start)
            if (isPalindrome(tempResult)) {
                return tempResult
            }
        }
    }

    return s[0]
};

const isPalindrome = (s) => {
    let left = 0
    let right = s.length - 1
    while (right >= left) {
        if (s[left] !== s[right])
            return false
        right--
        left++
    }

    return true
}


